import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { addItem, deleteItem, editItem } from './actions';
import Form from './components/Form';
import ShowAllCards from './ShowAllCards';
import InventoryCount from './components/InventoryCount';
import Navbar from './components/Navbar';
import inventory from './inventory';
import { FaSearch } from 'react-icons/fa';
import './App.css';

function App(props) {
  const { cards, addItem, deleteItem, editItem } = props;
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const transformedCards = inventory.map((curr, index) => ({
      id: `${index}-${Date.now()}`,
      name: curr.name,
      image: curr.image,
      price: curr.price,
      description: curr.description
    }));

    transformedCards.forEach(item => addItem(item));
  }, [addItem]);

  const handleAddItem = (item) => {
    addItem(item);
  };

  const handleDelete = (id) => {
    deleteItem(id);
  };

  const handleEdit = (id, updatedItem) => {
    editItem(id, updatedItem);
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const filteredCards = cards.filter(card =>
    card.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <>
      <Navbar />
      <Form addItemHandler={handleAddItem} />
      <br />
      <br />
      <InventoryCount />
      <br />
      <br />
      <div className="search-container">
        <input
          type="text"
          placeholder="Search cards"
          value={searchTerm}
          onChange={handleSearchChange}
          className="search-input"
        />
        <div className="search-icon">
          <FaSearch />
        </div>
      </div>
      <ShowAllCards cards={filteredCards} deleteHandler={handleDelete} editHandler={handleEdit} />
    </>
  );
}

const mapStateToProps = (state) => {
  return {
    cards: state.cards
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addItem: (item) => dispatch(addItem(item)),
    deleteItem: (id) => dispatch(deleteItem(id)),
    editItem: (id, updatedItem) => dispatch(editItem(id, updatedItem))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
