import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { connect } from 'react-redux';
import { addItem, deleteItem, editItem } from './actions';
import Form from './components/Form';
import ShowAllCards from './ShowAllCards';
import InventoryCount from './components/InventoryCount';
import Navbar from './components/Navbar';
import { FaSearch } from 'react-icons/fa';
import './App.css';

function App(props) {
  const { cards, addItem, deleteItem, editItem } = props;
  const [searchTerm, setSearchTerm] = useState('');
  const [sortOption, setSortOption] = useState('');

  useEffect(() => {
    axios.get('http://localhost:3000/api/inventory') // Update the port number if necessary
      .then(response => {
        const transformedCards = response.data;
        transformedCards.forEach(item => addItem(item));
      })
      .catch(error => {
        console.error('Error retrieving inventory:', error);
      });
  }, [addItem]);

  const handleAddItem = (item) => {
    axios.post('http://localhost:3000/api/inventory', item) // Update the port number if necessary
      .then(response => {
        const newItem = response.data;
        addItem(newItem);
      })
      .catch(error => {
        console.error('Error adding item:', error);
      });
      window.location.reload();
  };

  const handleDelete = (id) => {
    axios.delete(`http://localhost:3000/api/inventory/${id}`) // Update the port number if necessary
      .then(() => {
        deleteItem(id);
      })
      .catch(error => {
        console.error('Error deleting item:', error);
      });

  };

  const handleEdit = (id, updatedItem) => {
    axios.put(`http://localhost:3000/api/inventory/${id}`, updatedItem) // Update the port number if necessary
      .then(response => {
        const editedItem = response.data;
        editItem(id, editedItem);
      })
      .catch(error => {
        console.error('Error editing item:', error);
      });
  };

  const handleSearchChange = (e) => {
    const searchTerm = e.target.value;

    axios.get(`http://localhost:3000/api/inventory/search?searchTerm=${searchTerm}`)
      .then(response => {
        const filteredItems = response.data;
        setSearchTerm(e.target.value);
      })
      .catch(error => {
        console.error('Error searching inventory:', error);
      });

    setSearchTerm(searchTerm);
  };

  const handleSortChange = (e) => {
    const option = e.target.value;
    setSortOption(option);

    // Make a request to the server to get the sorted data
    axios.get(`http://localhost:3000/api/inventory?sort=${option}`)
      .then(response => {
        const sortedItems = response.data;
        // Update the Redux state with the sorted items
        // ...
      })
      .catch(error => {
        console.error('Error sorting inventory:', error);
      });
  };

  let filteredCards = cards.filter(card =>
    card.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (sortOption === 'price_asc') {
    filteredCards = filteredCards.sort((a, b) => a.price - b.price);
  } else if (sortOption === 'price_desc') {
    filteredCards = filteredCards.sort((a, b) => b.price - a.price);
  }

  return (
    <>
      <Navbar />
      <Form addItemHandler={handleAddItem} />
      <br />
      <br />
      <InventoryCount />
      <br />
      <br />
      <div className="search-container">
        <input
          type="text"
          placeholder="Search cards"
          value={searchTerm}
          onChange={handleSearchChange}
          className="search-input"
        />
        <div className="search-icon">
          <FaSearch />
        </div>
      </div>
      <div className="sort-container">
        <label className="sort-label" htmlFor="sort-dropdown">Sort by:</label>
        <select
          id="sort-dropdown"
          value={sortOption}
          onChange={handleSortChange}
          className="sort-dropdown"
        >
          <option value="">Select an option</option>
          <option value="price_asc">Price (Low to High)</option>
          <option value="price_desc">Price (High to Low)</option>
        </select>
      </div>
      <ShowAllCards cards={filteredCards} deleteHandler={handleDelete} editHandler={handleEdit} />
    </>
  );
}

const mapStateToProps = (state) => {
  return {
    cards: state.cards
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    addItem: (item) => dispatch(addItem(item)),
    deleteItem: (id) => dispatch(deleteItem(id)),
    editItem: (id, updatedItem) => dispatch(editItem(id, updatedItem))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
